import { useContext } from 'react';
import { Container, Row, Col, Button, Image } from 'react-bootstrap';
import Link from 'next/link';
import Slider from 'react-slick';

import AppContext from '../AppContext';

function FeaturedCategories(props) {
  const { categories } = useContext(AppContext);
  const settings = {
    dots: false,
    infinite: false,
    speed: 500,
    slidesToShow: 6,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 991,
        settings: {
          slidesToShow: 4,
          slidesToScroll: 3,
        }
      },
    ]
  };

  return (
    <div className="FeaturedCategories">
      <Container>
        <Row>
          <Col>
            <div className="FeaturedCategories-BoxWarp">
              <Slider {...settings}>
                {
                  categories.map((index, category) => (
                    <div key={category.id}>
                      {
                        categories.length > index ?
                          <Link 
                            href="!#" 
                            className="FeaturedCategories-Item"
                          >
                            <Image 
                              fluid 
                              alt={category.title}
                              className="FeaturedCategories-Item__Image" 
                              src={`./static/images/categories/${category.image}`} 
                            />
                            <p className="FeaturedCategories-Item__Title">{category.title}</p>
                          </Link> :
                          <Button className="FeaturedCategories-Item">
                            <Image 
                              fluid 
                              alt={category.title}
                              className="FeaturedCategories-Item__Image" 
                              src={`./static/images/categories/${category.image}`} 
                            />
                            <p className="FeaturedCategories-Item__Title">{category.title}</p>
                          </Button>
                      }
                    </div>
                  ))
                }
              </Slider>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default FeaturedCategories;
