import { useContext } from 'react';
import { Button, Image } from 'react-bootstrap';
import Select from 'react-select';

import AppContext from '../AppContext';

function HeroBanner(props) {
  const { searchOptions } = useContext(AppContext);

  return (
    <div className="HeroBanner" style={{ backgroundImage: 'url(./static/images/hero-banner.jpg)' }}>
      <div className="HeroBanner-Warp">
        <h1 className="HeroBanner-Heading">Your Personal Assistant</h1>
        <h3 className="HeroBanner-SubHeading">One-stop solution for your services. Order any service, anytime.</h3>
        <div className="HeroBanner-Actions">
          <div className="HeroBanner-Actions__LocationPicker">
            <Button
              type="button"
              className="HeroBanner-Actions__LocationPicker--Btn"
            >
              <Image src="./static/images/location-icon.svg" /> Gulshan
            </Button>
          </div>
          <div className="HeroBanner-Actions__Search">
            {/* <input 
              type="text" 
              className="HeroBanner-Actions__Search--Input"
              placeholder="Find your service here e.g. AC, Car, Facial …" 
            /> */}
            <Select
              // value={selectedOption}
              // onChange={this.handleChange}
              className="HeroSelect"
              classNamePrefix="HeroSelect"
              placeholder="Find your service here e.g. AC, Car, Facial…" 
              options={searchOptions}
            />
            <Button
              variant="primary"
              className="HeroBanner-Actions__Search--Btn"
            ><i className="material-icons">search</i></Button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default HeroBanner;
