import { Row, Col, Image } from 'react-bootstrap';
import Slider from 'react-slick';

function TestimonialSlider( props ) {
  const settings = {
    dots: false,
    infinite: false,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };
  const testimonials = props.testimonials;

  return (
    <div className="Testimonial-Slider">
      <Slider {...settings}>
        {
          testimonials.length > 0 &&
            testimonials.map(testimonial => (
              <div key={testimonial.id}>
                <Row>
                  <Col md={6}>
                    <div className="Testimonial-Slider__InnerBox d-flex align-items-center h-100">
                      <div className="d-block">
                        <p className="Testimonial-Comment">{testimonial.comment}</p>
                        <p className="Testimonial-Comment__Client">- {testimonial.name}</p>
                        <p className="Testimonial-Comment__Profession">{testimonial.profession}</p>
                      </div>
                    </div>
                  </Col>
                  <Col md={6}>
                    <div 
                      className="Testimonial-VideoBanner d-flex justify-content-center align-items-center" 
                      style={{ backgroundImage: `url(./static/images/testimonials/${testimonial.image})` }}
                    >
                      <Image src="./static/images/play-btn.svg" className="Testimonial-VideoBanner__PlayBtn" />
                    </div>
                  </Col>
                </Row>
              </div>
            ))
        }
      </Slider>
    </div>
  );
}

export default TestimonialSlider;
