import React, { Component } from 'react';
import AppContext from './AppContext';

class AppProvider extends Component {
  render() {
    return (
      <AppContext.Provider
        value={{
          isMobile: props.isMobile
        }}
      >
        {props.children}
      </AppContext.Provider>
    );
  }
}

