import { useContext } from 'react';
import { Row, Col, Image } from 'react-bootstrap';
import Slider from 'react-slick';

import AppContext from '../AppContext';

function TestimonialSlider(props) {
  const settings = {
    dots: false,
    infinite: false,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };
  const { crucials } = useContext(AppContext);

  return (
    <div className="Testimonial-Slider">
      <Slider {...settings}>
        {
          crucials.length > 0 && 
          crucials.map(crucial => (
            <div key={crucial.id}>
              <Row>
                <Col md={6}>
                  <div className="Testimonial-Slider__InnerBox d-flex align-items-center h-100">
                    <div className="d-block">
                      <p className="Testimonial-Comment">{crucial.comment}</p>
                      <p className="Testimonial-Comment__Client">- {crucial.name}</p>
                      <p className="Testimonial-Comment__Profession">{crucial.profession}</p>
                    </div>
                  </div>
                </Col>
                <Col md={6}>
                  <div 
                    className="Testimonial-VideoBanner d-flex justify-content-center align-items-center" 
                    style={{ backgroundImage: `url(./static/images/testimonials/${crucial.image})` }}
                  >
                    <Image src="./static/images/play-btn.svg" className="Testimonial-VideoBanner__PlayBtn" />
                  </div>
                </Col>
              </Row>
            </div>
          ))
        }
      </Slider>
    </div>
  );
}

export default TestimonialSlider;
