import { useState } from 'react';
import { Row, Col, Image } from 'react-bootstrap';
import Slider from 'react-slick';

const testimonials = [
  {
    comment: 'Such service platforms are available in other countries. I’ve personally used those when I was abroad. I’m very pleased that such a portal is available here in Bangladesh as well. Thank you Sheba.xyz.',
    name: 'Zabeen Yusuf Nur',
    profession: 'IT Consultant, Australia',
    image: '01.jpg'
  },
  {
    comment: 'It was my marriage and wasn’t getting schedule from any beauty parlor. So, I downloaded Sheba.xyz app and found that it has all of my requirements. The beautician arrived in time and was really amazing. Thank you.',
    name: 'Zeba Fariba',
    profession: 'Management Trainee',
    image: '02.jpg'
  }
];

function TestimonialSlider(props) {
  const [crucial, setCrucial] = useState({
    comment: '',
    name: '',
    profession: '',
    image: ''
  });

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  return (
    <div className="Testimonial-Slider">
      <Slider {...settings}>
        <div>
          <Row>
            <Col md={6}>
              <div className="Testimonial-Slider__InnerBox d-flex align-items-center h-100">
                <div className="d-block">
                  <p className="Testimonial-Comment">
                    Such service platforms are available in other countries. I’ve personally used those when I was abroad. I’m very pleased that such a portal is available here in Bangladesh as well. Thank you Sheba.xyz.
                  </p>
                  <p className="Testimonial-Comment__Client">- Zabeen Yusuf Nur</p>
                  <p className="Testimonial-Comment__Profession">IT Consultant, Australia</p>
                </div>
              </div>
            </Col>
            <Col md={6}>
              <div 
                className="Testimonial-VideoBanner d-flex justify-content-center align-items-center" 
                style={{ backgroundImage: 'url(./static/images/testimonials/01.jpg)' }}
              >
                <Image src="./static/images/play-btn.svg" className="Testimonial-VideoBanner__PlayBtn" />
              </div>
            </Col>
          </Row>
        </div>
      </Slider>
    </div>
  );
}

export default TestimonialSlider;
